const app=new PIXI.Application({width:800,height:600,backgroundColor:1087931});const container=new PIXI.Container;app.stage.addChild(container);const texture=PIXI.Texture.from("cat.png");const sprite=new PIXI.Sprite(texture);sprite.anchor.set(.5);container.addChild(sprite);container.x=app.screen.width/2;container.y=app.screen.height/2;container.pivot.x=container.width/2;container.pivot.y=container.height/2;app.ticker.add(delta=>{container.rotation-=.01*delta});